mapper.select:
select

from tb_notice
where n_status = #{status}
and n_type  = #{type}

order by update_time desc
limit 1

select

from tb_notice n left join tb_room_notice rn on rn.notice_id = n.id
where rn.room_id = #{roomId}
and n.n_status = #{status}
and n.n_type  = #{type}
order by n.update_time desc

select
from tb_notice where n_type = #{type}
and CONCAT(IFNULL(`n_title`,''),IFNULL(`n_content`,'')) LIKE CONCAT('%', #{key}, '%')
